package com.lc.boxuegu.m_findAnagrams;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;
import java.util.Objects;

/**
 * @author zhaiyj
 * @date 2025/4/23 20:31
 */
public class Solution {

    public static void main(String[] args) {
        String s = "";
        String p = "yqrbgjdwtcaxzsnifvhmou";

        Solution solution = new Solution();
        List<Integer> anagrams = solution.findAnagrams(s, p);
        System.out.println(anagrams);

    }

    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> ans = new ArrayList<>();
        int[] cnt = new int[26]; // 统计 p 的每种字母的出现次数
        for (char c : p.toCharArray()) {
            cnt[c - 'a']++;
        }


        int left = 0;
        for (int right = 0; right < s.length(); right++){
            int c = s.charAt(right) - 'a';
            cnt[c]--;
            while (cnt[c] < 0){
                cnt[s.charAt(left) - 'a'] ++;
                left++;
            }

            if(right - left + 1 == p.length()){
                ans.add(left);
            }

        }
        return ans;
    }

    public List<Integer> findAnagrams2(String s, String p) {
        List<Integer> ans = new ArrayList<>();
        int[] cntP = new int[26];
        int[] cntS = new int[26];
        for (char c : p.toCharArray()) {
            cntP[c - 'a']++;
        }

        for (int right = 0; right < s.length(); right++){
            cntS[s.charAt(right) - 'a'] ++;
            int left = right - p.length() + 1;
            if(left < 0){
                continue;
            }

            if(Arrays.equals(cntP, cntS)){
                ans.add(left);
            }
            cntS[s.charAt(left) - 'a'] --;
        }
        return ans;
    }


    public List<Integer> findAnagrams1(String s, String p) {
        int sl = s.length();
        int pl = p.length();

        char[] charArray = p.toCharArray();
        Arrays.sort(charArray);
        String xp = new String(charArray);
        List<Integer> result = new ArrayList<>();
        for(int i = 0; i < sl;i++){
            if(i > sl - pl){
                break;
            }
            String slice = s.substring(i, pl + i);
            char[] sliceChar = slice.toCharArray();

            Arrays.sort(sliceChar);
            if(Objects.equals(new String(sliceChar), xp )){
                result.add(i);
            }
        }
        return result;
    }

}
